// src/components/Settings.js  

import React, { useState } from 'react';  
import { useNavigate } from 'react-router-dom';  
import styles from '../styles/Settings.module.css';  
import profile from '../profile.png';  
import { FaBell } from 'react-icons/fa';  
import Sidebar from 'c'; // Importer le sidebar  

const Settings = () => {  
  const [userName, setUserName] = useState('');  
  const [email, setEmail] = useState('');  
  const [password, setPassword] = useState('');  
  const [confirmPassword, setConfirmPassword] = useState('');  
  const [threshold, setThreshold] = useState('');  
  const [pricePerKw, setPricePerKw] = useState('');  

  const navigate = useNavigate();   

  const handleSubmit = (e) => {  
    e.preventDefault();  
    console.log({ userName, email, password, threshold, pricePerKw });  
  };  

  const handleSignOut = () => {  
    localStorage.removeItem('token');   
    navigate('/login');   
  };  

  return (  
    <div className={styles.appContainer}>  
      <Sidebar onSignOut={handleSignOut} />  
      <div className={styles.settingsContainer}>  
        <div className={styles.header}>  
          <h2>Settings</h2>  
          <div className={styles.notification}>  
            <FaBell className={styles.notificationIcon} />   
            <img src={profile} alt="Profile" style={{ width: '12%', height: 'auto' }} />  
          </div>  
        </div>  
        <hr />  
        <form onSubmit={handleSubmit}>  
          <div className={styles.profileSection}>  
            <div className={styles.profileImage}>  
              ðŸ‘¤  
            </div>  
            <div className={styles.inputGroup}>  
              <input  
                type="text"  
                placeholder="User Name"  
                value={userName}  
                onChange={(e) => setUserName(e.target.value)}  
                className={styles.input}  
                required  
              />  
              <input  
                type="email"  
                placeholder="Email"  
                value={email}  
                onChange={(e) => setEmail(e.target.value)}  
                className={styles.input}  
                required  
              />  
              <input  
                type="password"  
                placeholder="Password"  
                value={password}  
                onChange={(e) => setPassword(e.target.value)}   
                className={styles.input}   
                required  
              />  
              <input  
                type="password"  
                placeholder="Confirm Password"  
                value={confirmPassword}  
                onChange={(e) => setConfirmPassword(e.target.value)}  
                className={styles.input}  
                required  
              />  
            </div>  
          </div>  

          <div className={styles.settingsSection}>  
            <input  
              type="number"  
              placeholder="Threshold in kW"  
              value={threshold}  
              onChange={(e) => setThreshold(e.target.value)}  
              required className={styles.input}  
            />  
            <input  
              type="number"  
              placeholder="Price of 1 kW"  
              value={pricePerKw}  
              onChange={(e) => setPricePerKw(e.target.value)}  
              className={styles.input}  
              required  
            />  
          </div>  

          <button className={styles.button} type="submit">Save Changes</button>  
        </form>  
      </div>  
    </div>  
  );  
};  

export default Settings;
