// AuthInterface.js  
import React, { useState } from 'react';  
import { Link, useNavigate } from 'react-router-dom';  // Importer useNavigate  
import { motion } from 'framer-motion';  
import './AuthInterface.css';  

function AuthInterface() {  
  const [email, setEmail] = useState('');  
  const [password, setPassword] = useState('');  
  const navigate = useNavigate(); // Initialiser useNavigate  

  const handleSignIn = async (event) => {  
    event.preventDefault();  
    
    try {  
      const response = await fetch('http://localhost:5000/api/login', {  
        method: 'POST',  
        headers: {  
          'Content-Type': 'application/json',  
        },  
        body: JSON.stringify({ email, password }),  
      });  

      const data = await response.json();  
      alert(data.message); // Afficher un message de confirmation ou d'erreur  
      
      if (response.ok) {  
        // Rediriger vers la page /dashboard après une connexion réussie  
        navigate('/dashboard');  
      }  
    } catch (error) {  
      console.error("Erreur lors de la connexion :", error);  
      alert('Une erreur est survenue. Veuillez réessayer.');  
    }  
  };  

  return (  
    <motion.div  
      initial={{ x: '-100%' }}  
      animate={{ x: 0 }}  
      exit={{ x: '100%' }}  
      transition={{ duration: 0.5 }}  
      className="auth-container"  
    >  
      <div className="info-container">  
        <h2>New Here?</h2>  
        <p>Create your account to get started.</p>  
        <Link to="/signup">  
          <button className="sign-up-button1">Sign Up</button>  
        </Link>  
      </div>  

      <div className="form-container">  
        <div className="form">  
          <h2>Welcome Back!</h2>  
          <p>Please login to your account.</p>  
          
          <input   
            type="text"   
            placeholder="Email Or User Name"   
            value={email}  
            onChange={(e) => setEmail(e.target.value)}   
          />  
          
          <input   
            type="password"   
            placeholder="Password"   
            value={password}  
            onChange={(e) => setPassword(e.target.value)}   
          />  
          <Link to="#" className="forgot-password">Forgot your password?</Link>  
          <button className="sign-in-button1" onClick={handleSignIn}>Sign In</button>  
        </div>  
      </div>  
    </motion.div>  
  );  
}  

export default AuthInterface;
