import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import styles from '../styles/AuthInterface.module.css'; // Import des styles CSS Modules

function AuthInterface() {
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignIn = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/signin', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email: usernameOrEmail, password })
    });
    const data = await response.json();
    if (response.ok) {
      localStorage.setItem('token', data.token); // Stockage du token
      alert('Login successful');
      navigate('/dashboard');
    } else {
      alert(data.message);
    }
  };

  return (
    <motion.div
      initial={{ x: '-100%' }}
      animate={{ x: 0 }}
      exit={{ x: '100%' }}
      transition={{ duration: 0.5 }}
      className={styles['auth-container']} // CSS Modules : utilisation avec styles
    >
      <div className={styles['info-container']}>
        <h2>New Here?</h2>
        <p>Create your account to get started.</p>
        <Link to="/signup">
          <button className={styles['sign-up-button1']}>Sign Up</button>
        </Link>
      </div>

      <div className={styles['form-container']}>
        <div className={styles['form']}>
          <h2>Welcome Back!</h2>
          <p>Please login to your account.</p>

          <input
            type="text"
            placeholder="Email Or User Name"
            value={usernameOrEmail}
            onChange={(e) => setUsernameOrEmail(e.target.value)}
          />

          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <Link to="#" className={styles['forgot-password']}>
            Forgot your password?
          </Link>
          <button className={styles['sign-in-button1']} onClick={handleSignIn}>Sign In</button>
        </div>
      </div>
    </motion.div>
  );
}

export default AuthInterface;
