import React from 'react';  
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';  
import AuthInterface from './AuthInterface';  
import SignUpPage from './SignUpPage';  
import Settings from './Settings'; // Importation du composant Settings  
import { useAuth } from './auth'; // Supposons que vous ayez un hook qui vérifie l'authentification  

function App() {  
  const { isAuthenticated } = useAuth(); // Hook personnalisé pour vérifier l'authentification  

  return (  
    <Router>  
      <Routes>  
        <Route path="/login" element={isAuthenticated ? <Navigate replace to="/settings" /> : <AuthInterface />} />  
        <Route path="/signup" element={<SignUpPage />} />  
        <Route   
          path="/settings"   
          element={isAuthenticated ? <Settings /> : <Navigate replace to="/login" />} // Protection de la route Settings  
        />  
        <Route path="/" element={<Navigate replace to="/login" />} />  
      </Routes>  
    </Router>  
  );  
}  

export default App;
