import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import styles from '../src/styles/AuthInterface.module.css'; // CSS Modules

function SignUpPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState(''); // State for password confirmation
  const [errorMessage, setErrorMessage] = useState(''); // State for error messages
  const [successMessage, setSuccessMessage] = useState(''); // State for success messages

  const handleSignUp = async (e) => {
    e.preventDefault();

    // Reset error message before validation
    setErrorMessage('');
    setSuccessMessage('');

    // Check if passwords match
    if (password !== confirmPassword) {
      setErrorMessage('Passwords do not match!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccessMessage(data.message); // Display success message
      } else {
        setErrorMessage(data.message); // Handle server-side errors
      }
    } catch (error) {
      setErrorMessage('An error occurred. Please try again later.');
      console.error(error); // Log error for debugging
    }
  };

  return (
    <motion.div
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '100%' }}
      transition={{ duration: 0.5 }}
      className={styles['auth-container']}
    >
      <div className={styles['info-container']}>
        <h2>Hello Again?</h2>
        <p>To keep connected with us please login with your personal info</p>
        <Link to="/login">
          <button className={styles['sign-in-button2']}>Sign In</button>
        </Link>
      </div>

      <div className={styles['form-container']}>
        <div className={styles['form']}>
          <h2>Create Your Account</h2>

          {errorMessage && <p className={styles['error']}>{errorMessage}</p>}
          {successMessage && <p className={styles['success']}>{successMessage}</p>}

          <input
            type="text"
            placeholder="Choose a username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Choose a password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <input
            type="password"
            placeholder="Confirm your password" // Confirm password field
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <button className={styles['sign-up-button2']} onClick={handleSignUp}>
            Sign Up
          </button>
        </div>
      </div>
    </motion.div>
  );
}

export default SignUpPage;
