import React, { useState } from 'react';  
import { Link, useNavigate } from 'react-router-dom'; // Importing useNavigate for redirection after login  
import { motion } from 'framer-motion';  
import styles from '../src/styles/AuthInterface.module.css'; // Import CSS Modules  

function AuthInterface() {  
  const [usernameOrEmail, setUsernameOrEmail] = useState('');  
  const [password, setPassword] = useState('');  
  const [errorMessage, setErrorMessage] = useState(''); // State for error messages  
  const navigate = useNavigate(); // Hook for redirection  

  const handleSignIn = async (e) => {  
    e.preventDefault();  
    setErrorMessage(''); // Reset error message  

    try {  
      const response = await fetch('http://localhost:5000/api/signin', {  
        method: 'POST',  
        headers: { 'Content-Type': 'application/json' },  
        body: JSON.stringify({ email: usernameOrEmail, password })  
      });  

      const data = await response.json();  

      if (response.ok) {  
        localStorage.setItem('token', data.token); // Store the token  
        alert('Login successful');  
        navigate('/home'); // Redirect to home page or dashboard after successful login  
      } else {  
        setErrorMessage(data.message || 'An error occurred. Please try again.'); // Error message  
      }  
    } catch (error) {  
      console.error('Error during sign in:', error);  
      setErrorMessage('An error occurred. Please try again later.'); // Generic message  
    }  
  };  

  return (  
    <motion.div  
      initial={{ x: '-100%' }}  
      animate={{ x: 0 }}  
      exit={{ x: '100%' }}  
      transition={{ duration: 0.5 }}  
      className={styles['auth-container']} // CSS Modules usage  
    >  
      <div className={styles['info-container']}>  
        <h2>New Here?</h2>  
        <p>Create your account to get started.</p>  
        <Link to="/signup">  
          <button className={styles['sign-up-button1']}>Sign Up</button>  
        </Link>  
      </div>  

      <div className={styles['form-container']}>  
        <div className={styles['form']}>  
          <h2>Welcome Back!</h2>  
          <p>Please login to your account.</p>  

          {errorMessage && (  
            <div className={styles['error-message']}>{errorMessage}</div> // Display error message  
          )}  

          <input  
            type="text"  
            placeholder="Email Or User Name"  
            value={usernameOrEmail}  
            onChange={(e) => setUsernameOrEmail(e.target.value)}  
            required // Ensure the field is required  
          />  

          <input  
            type="password"  
            placeholder="Password"  
            value={password}  
            onChange={(e) => setPassword(e.target.value)}  
            required // Ensure the field is required  
          />  

          <Link to="#" className={styles['forgot-password']}>  
            Forgot your password?  
          </Link>  
          <button className={styles['sign-in-button1']} onClick={handleSignIn}>Sign In</button>  
        </div>  
      </div>  
    </motion.div>  
  );  
}  

export default AuthInterface;
