// AuthInterface.js
import React, { useState } from 'react';  // Import useState pour gérer l'état des inputs
import { Link, useNavigate } from 'react-router-dom';  // Import useNavigate pour rediriger après connexion
import { motion } from 'framer-motion';  // Import Framer Motion
import './AuthInterface.css';

function AuthInterface() {
  const [email, setEmail] = useState('');  // État pour l'email
  const [password, setPassword] = useState('');  // État pour le mot de passe
  const navigate = useNavigate();  // Utilisé pour rediriger après connexion

  // Fonction de gestion de la connexion
  const handleLogin = async (e) => {
    e.preventDefault();
    
    const response = await fetch('http://localhost:3001/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),  // Envoie les informations saisies au backend
    });

    const data = await response.json();
    if (response.ok && data.token) {
      localStorage.setItem('token', data.token);  // Stocke le token JWT
      navigate('/dashboard');  // Redirige vers le tableau de bord après connexion
    } else {
      alert('Login failed');
    }
  };

  return (
    <motion.div
      initial={{ x: '-100%' }}  // Animation de glissement
      animate={{ x: 0 }}
      exit={{ x: '100%' }}
      transition={{ duration: 0.5 }}
      className="auth-container"
    >
      <div className="info-container">
        <h2>New Here?</h2>
        <p>Create your account to get started.</p>
        <Link to="/signup">
          <button className="sign-up-button1">Sign Up</button>
        </Link>
      </div>

      <div className="form-container">
        <div className="form">
          <h2>Welcome Back!</h2>
          <p>Please login to your account.</p>
          
          {/* Formulaire de connexion */}
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Email Or User Name"
              value={email}
              onChange={(e) => setEmail(e.target.value)}  // Mets à jour l'email saisi
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}  // Mets à jour le mot de passe saisi
            />
            <Link to="#" className="forgot-password">Forgot your password?</Link>
            <button type="submit" className="sign-in-button1">Sign In</button>
          </form>
        </div>
      </div>
    </motion.div>
  );
}

export default AuthInterface;
