// Dashboard.js  
import React from 'react';  
import  './DashboardDesign.css'
import { Line } from 'react-chartjs-2';  
import {  
    Chart as ChartJS,  
    LineElement,  
    PointElement,  
    LinearScale,  
    CategoryScale,  
    Title,  
    Tooltip,  
    Legend,  
} from 'chart.js';  

// Enregistre les échelles et les éléments nécessaires  
ChartJS.register(LineElement, PointElement, LinearScale, CategoryScale, Title, Tooltip, Legend);  

const Dashboard = () => {  
    // Exemple de données pour le graphique  
    const data = {  
        labels: ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5'],  
        datasets: [  
            {  
                label: 'Energy Consumption',  
                data: [200, 300, 150, 400, 350],  
                fill: false,  
                backgroundColor: 'rgba(75,192,192,1)',  
                borderColor: 'rgba(75,192,192,0.4)',  
            },  
        ],  
    };  

    return (  
        <div className="dashboard-container">  
            <header className="dashboard-header">  
                <h1>Dashboard</h1>  
                <p>Hello, Jihen Retib</p>  
            </header>  
            <div className="chart-container">  
                <Line data={data} />  
            </div>  
            <div className="dashboard-cards">  
                <div className="card">  
                    <h3>Consumption</h3>  
                    <p>60% Done</p>  
                </div>  
                <div className="card">  
                    <h3>To Pay</h3>  
                    <p>100 DT</p>  
                </div>  
                <div className="card">  
                    <h3>Devices</h3>  
                    <p>5 Active Devices</p>  
                </div>  
                <div className="card">  
                    <h3>Activity</h3>  
                    <p>26.8° C</p>  
                </div>  
            </div>  
            <nav className="sidebar">  
                <h2>Dashboard</h2>  
                <ul>  
                    <li>Consumption</li>  
                    <li>Billing</li>  
                    <li>Settings</li>  
                    <li>Sign Out</li>  
                </ul>  
            </nav>  
        </div>  
    );  
};  

export default Dashboard;
