// SignUpPage.js
import React, { useState } from 'react';  // Import useState pour gérer les champs du formulaire
import { Link, useNavigate } from 'react-router-dom';  // Import useNavigate pour rediriger après l'inscription
import { motion } from 'framer-motion';
import './AuthInterface.css';

function SignUpPage() {
  const [email, setEmail] = useState('');  // État pour l'email
  const [username, setUsername] = useState('');  // État pour le nom d'utilisateur
  const [password, setPassword] = useState('');  // État pour le mot de passe
  const [confirmPassword, setConfirmPassword] = useState('');  // État pour la confirmation du mot de passe
  const navigate = useNavigate();  // Utilisé pour rediriger après inscription

  // Fonction de gestion de l'inscription
  const handleSignUp = async (e) => {
    e.preventDefault();
    
    if (password !== confirmPassword) {
      alert("Passwords don't match");
      return;
    }

    const response = await fetch('http://localhost:3001/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, username, password }),  // Envoie les informations saisies au backend
    });

    if (response.ok) {
      alert('User created successfully');
      navigate('/login');  // Redirige vers la page de connexion après inscription
    } else {
      alert('Sign up failed');
    }
  };

  return (
    <motion.div
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '100%' }}
      transition={{ duration: 0.5 }}
      className="auth-container"
    >
      <div className="info-container">
        <h2>Hello Again?</h2>
        <p>
          To keep connected with us please login with your personal info
        </p>
        <Link to="/login">
          <button className="sign-in-button2">Sign In</button>
        </Link>
      </div>

      <div className="form-container">
        <div className="form">
          <h2>Create Your Account</h2>
          
          {/* Formulaire d'inscription */}
          <form onSubmit={handleSignUp}>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}  // Mets à jour l'email
            />
            <input
              type="text"
              placeholder="Choose a username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}  // Mets à jour le nom d'utilisateur
            />
            <input
              type="password"
              placeholder="Choose a password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}  // Mets à jour le mot de passe
            />
            <input
              type="password"
              placeholder="Confirm your password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}  // Mets à jour la confirmation du mot de passe
            />
            <button type="submit" className="sign-up-button2">Sign Up</button>
          </form>
        </div>
      </div>
    </motion.div>
  );
}

export default SignUpPage;
